// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

/* Graph of inheritance
    A
   / \
  B   C
 / \ /
F  D,E

*/

contract A {
    function foo() public pure virtual returns (string memory) {
        return "A";
    }
}

// Contracts inherit other contracts by using the keyword 'is'.
contract B is A {
    // Override A.foo()
    function foo() public pure virtual override returns (string memory) {
        return "B";
    }
}

contract C is A {
    // Override A.foo()
    function foo() public pure virtual override returns (string memory) {
        return "C";
    }
}

// Contracts can inherit from multiple parent contracts.
// When a function is called that is defined multiple times in
// different contracts, parent contracts are searched from
// right to left, and in depth-first manner.

contract D is B, C {
    // D.foo() returns "C"
    // since C is the right most parent contract with function foo()
      // æ„é€ çš„é¡ºåºæ˜¯ä»å·¦åˆ°å³ï¼Œå³ï¼šBï¼ŒCï¼ŒD
      // æœ€ç»ˆç»§æ‰¿çš„é¡ºåºæ˜¯ï¼šæœ€è¿œç»§æ‰¿
    function foo() public pure override(B, C) returns (string memory) {
        return super.foo();
    }
}

contract E is C, B {
    // E.foo() returns "B"
    // since B is the right most parent contract with function foo()
    function foo() public pure override(C, B) returns (string memory) {
        return super.foo();
    }
}

// Inheritance must be ordered from â€œmost base-likeâ€ to â€œmost derivedâ€.
// Swapping the order of A and B will throw a compilation error.
// å½“ç»§æ‰¿çš„çˆ¶åˆçº¦ä¸­æœ‰ç»§æ‰¿å…³ç³»æ—¶ï¼Œéœ€è¦å…ˆç»§æ‰¿åŸºç±»ï¼Œå†ç»§æ‰¿è¡ç”Ÿç±»
contract F is A, B {
    function foo() public pure override(A, B) returns (string memory) {
        return super.foo();
    }
}

//æŠ¥é”™ï¼Œå› ä¸ºç»§æ‰¿é¡ºåºå¯¼è‡´
contract G is B, A {
    function foo() public pure override(A, B) returns (string memory) {
        return super.foo();
